
public class L5Q7c {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) 
		{ 
			for(int k=5-i;k>=1;k--) 
			{ 
				System.out.print("  ");
			} 
			for(int j=1;j<=i;j++) 
			{
				System.out.print(" "+i); 
			} 
			System.out.println();
		} 

	}

}
